<?= ((in_array('status', $columns) == true)?"<th>Status</th>":"") ?>                                    </tr>                                    </thead>                                    <tbody>                                    <?php                                    //Showing Customers Data                                    foreach($tankers as $tanker){                                        $fitness_certificate = ($tanker->fitness_certificate == 0)?"No" : "Yes";                                        if($tanker->free == true){                                            $status = "Free";                                        }else{                                            $status = "On Move";                                            $status = $status."<span style='font-size: 11px;'>";                                            $status = $status." (".$tanker->current_trip.") ";                                            $status = $status."".$this->carbon->createFromFormat('Y-m-d',$tanker->trip_date)->toFormattedDateString();                                            $status = $status."</span>";                                        }                                        //$status = ($tanker->free == true)?"<span style='color: green'>Free</span>":"<span style='color: red'>On Move</span>";                                        ?>                                        <tr>                                            <?= ((in_array('id', $columns) == true)?"<th>$tanker->id</th>":"") ?>                                            <?= ((in_array('tanker', $columns) == true)?"<th>$tanker->truck_number</th>":"") ?>                                            <?= ((in_array('customer', $columns) == true)?"<th>$tanker->customerName</th>":"") ?>                                            <?= ((in_array('engine', $columns) == true)?"<th>$tanker->engine_number</th>":"") ?>                                            <?= ((in_array('chase', $columns) == true)?"<th>$tanker->chase_number</th>":"") ?>                                            <?= ((in_array('capacity', $columns) == true)?"<th>$tanker->capacity</th>":"") ?>                                            <?= ((in_array('certificate', $columns) == true)?"<th>$fitness_certificate</th>":"") ?>                                            <?= ((in_array('status', $columns) == true)?"<th>$status</th>":"") ?>                                        </tr>                                    <?php                                    }                                    ?>                                    </tbody>                                </table>                            </div>                        </div>                    </div>                </div>            </div>        </div>    </div></div><script src="<?= js()?>sorttable.js"></script></body></html>