anker', $columns) == true): ?>                                                    <td> <?= $trip->tanker->tanker_number; ?> </td>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('capacity', $columns) == true): ?>                                                    <td> <?= $trip->tanker->capacity; ?> </td>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('contractor', $columns) == true): ?>                                                    <td> <?= $trip->contractor->name; ?> </td>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('customer', $columns) == true): ?>                                                    <td> <?= $trip->customer->name; ?> </td>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('company', $columns) == true): ?>                                                    <td> <?= $trip->company->name; ?> </td>                                                <?php endif; ?>                                                <!--first column-->                                                <?php if(in_array('source', $columns) == true): ?>                                                    <td style="font-size: 11px;"><?= $detail->source->name; ?></td>                                                <?php endif; ?>                                                <!--first column-->                                                <!--first column-->              